import React, { useState } from "react";
import "./GameForm.css";

function GameForm({ onSubmit }) {
  const [newGame, setNewGame] = useState({
    title: "",
    platform: "",
    coverUrl: "",
    rating: 0,
    hoursPlayed: 0,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewGame({ ...newGame, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(newGame);
    setNewGame({ title: "", platform: "", coverUrl: "", rating: 0, hoursPlayed: 0 });
  };

  return (
    <form className="game-form card" onSubmit={handleSubmit}>
      <h2>üéÆ Agregar nuevo juego</h2>

      <div className="form-group">
        <label>T√≠tulo</label>
        <input
          type="text"
          name="title"
          value={newGame.title}
          onChange={handleChange}
          required
          placeholder="Ej: The Legend of Zelda"
        />
      </div>

      <div className="form-group">
        <label>Plataforma</label>
        <input
          type="text"
          name="platform"
          value={newGame.platform}
          onChange={handleChange}
          required
          placeholder="Ej: Nintendo Switch"
        />
      </div>

      <div className="form-group">
        <label>URL de portada</label>
        <input
          type="url"
          name="coverUrl"
          value={newGame.coverUrl}
          onChange={handleChange}
          placeholder="https://..."
        />
      </div>

      <div className="form-row">
        <div className="form-group">
          <label>Horas jugadas</label>
          <input
            type="number"
            name="hoursPlayed"
            value={newGame.hoursPlayed}
            onChange={handleChange}
            min="0"
          />
        </div>

        <div className="form-group">
          <label>Puntuaci√≥n ‚≠ê</label>
          <input
            type="number"
            name="rating"
            value={newGame.rating}
            onChange={handleChange}
            min="0"
            max="5"
            step="1"
          />
        </div>
      </div>

      <button type="submit" className="submit-btn">Agregar Juego</button>
    </form>
  );
}

export default GameForm;
