import React, { useEffect, useState } from "react";
import "./ReviewList.css";

function ReviewList({ gameId }) {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        setLoading(true);
        setError(null);

        let url = "http://localhost:5000/api/reviews";
        if (gameId) url += `?gameId=${gameId}`;

        const response = await fetch(url);
        if (!response.ok) throw new Error("Error al cargar rese√±as");

        const data = await response.json();
        setReviews(data);
      } catch (error) {
        console.error("Error al cargar rese√±as:", error);
        setError("No se pudieron cargar las rese√±as üò¢");
      } finally {
        setLoading(false);
      }
    };

    fetchReviews();
  }, [gameId]);

  // üîπ Estados de carga y error
  if (loading) {
    return (
      <div className="review-list">
        <h2>Rese√±as</h2>
        <p className="review-message">Cargando rese√±as...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="review-list">
        <h2>Rese√±as</h2>
        <p className="review-message error">{error}</p>
      </div>
    );
  }

  return (
    <div className="review-list">
      <h2>Rese√±as</h2>

      {reviews.length === 0 ? (
        <p className="review-message">No hay rese√±as disponibles.</p>
      ) : (
        reviews.map((review, index) => (
          <div className="review-card" key={review._id || index}>
            <h3>{review.gameTitle || "Juego sin t√≠tulo"}</h3>
            <p className="review-text">{review.text || "Sin comentario"}</p>
            <p className="review-rating">‚≠ê {review.rating || 0}/5</p>
            <small>
              Escrito por: <strong>{review.user || "An√≥nimo"}</strong>
            </small>
          </div>
        ))
      )}
    </div>
  );
}

export default ReviewList;
